import React, { useState } from "react";
import { Nav, NavDropdown } from "react-bootstrap";
import { LinkContainer } from "react-router-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import { logout } from "../../Redux/Action/AuthAction";

const Header = () => {
  const [menu, setMenu] = useState(false);

  const openMenu = () => {
    setMenu(!menu);
  };
  if (menu === true) {
    var as = { display: "none" };
  } else {
    as = { display: "block" };
  }

  const userLogin = useSelector((state) => state.userLogin);
  const { userResult } = userLogin;

  const cart = useSelector((state) => state.cart);

  // console.log(cart);

  //fun for decimal
  const addDecimal = (num) => {
    return (Math.round(num * 100) / 100).toFixed(2);
  };
  cart.itemsPrice = addDecimal(
    cart.cartItems.reduce((acc, item) => acc + item.price * item.qty, 0)
  );

  cart.shippingPrice = addDecimal(cart.cartItems < 500 ? 0 : 50);
  cart.taxPrice = addDecimal(Number((0.18 * cart.itemsPrice).toFixed(2)));
  cart.totalPrice = (
    Number(cart.itemsPrice) +
    Number(cart.shippingPrice) +
    Number(cart.taxPrice)
  ).toFixed(2);

  const dispatch = useDispatch();

  const logoutHandler = () => {
    dispatch(logout());
  };
  return (
    <div>
      {/* Page Preloder */}
      <div id="preloder">
        <div className="loader" />
      </div>

      {/* Header Section Begin */}
      <header className="header">
        <div className="header__top" style={{ background: "#4795ff" }}>
          <div className="container">
            <div className="row top_menu">
              <div className="col-lg-6 col-md-6">
                <div className="header__top__left">
                  <ul
                    style={{ color: "#fff", marginTop: "8px" }}
                    className="mb-0"
                  >
                    <li>
                      <i className="fa fa-envelope" />
                      herbalproducts@gmail.com
                    </li>
                    <li>Welcome To Herbal Products</li>
                  </ul>
                </div>
              </div>
              <div className="col-lg-6 col-md-6">
                <div className="header__top__right">
                  <div className="header__top__right__social">
                    <Link to="">
                      <i className="fa fa-facebook" />
                    </Link>
                    <Link to="">
                      <i className="fa fa-twitter" />
                    </Link>
                    <Link to="">
                      <i className="fa fa-linkedin" />
                    </Link>
                    <Link to="">
                      <i className="fa fa-pinterest-p" />
                    </Link>
                  </div>

                  <div className="header__top__right__auth">
                    {userResult ? (
                      <NavDropdown title={userResult.name} id="username">
                        <LinkContainer to="/profile">
                          <NavDropdown.Item>Profile</NavDropdown.Item>
                        </LinkContainer>
                        <NavDropdown.Item onClick={logoutHandler}>
                          Logout
                        </NavDropdown.Item>
                      </NavDropdown>
                    ) : (
                      <LinkContainer to="/login">
                        <Nav.Link>
                          <i className="fas fa-user"></i>
                          &nbsp; Login
                        </Nav.Link>
                      </LinkContainer>
                    )}
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
        <div className="container header__top fluid">
          <div className="row">
            <div className="col-lg-3">
              <div className="header__logo">
                <Link to="/">
                  <h3>Herbal Products</h3>
                </Link>
              </div>
            </div>
            <div className="col-lg-6" >
              <nav className="header__menu">
                <ul>
                  <li className="active">
                    <Link to="/">Home</Link>
                  </li>
                  <li>
                    <Link to="/products">Products</Link>
                  </li>

                  <li>
                    <Link to="/cart">Cart</Link>
                  </li>
                  <li>
                    <Link to="/contact-us">Contact</Link>
                  </li>
                </ul>
              </nav>
            </div>
            <div className="col-lg-3">
              <div className="header__cart">
                <ul>
                  <li>
                    <Link to="/cart">
                      <i className="fa fa-shopping-bag" />
                      <span>{cart.cartItems.length}</span>
                    </Link>
                  </li>
                </ul>
                <div className="header__cart__price">
                  item: <span>{cart.totalPrice}</span>
                </div>
              </div>
            </div>
          </div>
          <div className="humberger__open">
            <i className="fa fa-bars" onClick={() => openMenu()}></i>
          </div>
        </div>
      </header>
    </div>
  );
};

export default Header;
