import React, { useState } from "react";
import { Nav, NavDropdown } from "react-bootstrap";
import { LinkContainer } from "react-router-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import { logout } from "../../Redux/Action/AuthAction";

const Header = () => {
  const [menu, setMenu] = useState(true);

  const openMenu = () => {
    setMenu(!menu);
  };
  if (menu === true) {
    var as = { display: "none" };
  } else {
    as = { display: "block" };
    console.log(menu);
  }

  const userLogin = useSelector((state) => state.userLogin);
  const { userResult } = userLogin;

  const cart = useSelector((state) => state.cart);

  // console.log(cart);

  //fun for decimal
  const addDecimal = (num) => {
    return (Math.round(num * 100) / 100).toFixed(2);
  };
  cart.itemsPrice = addDecimal(
    cart.cartItems.reduce((acc, item) => acc + item.price * item.qty, 0)
  );

  cart.shippingPrice = addDecimal(cart.cartItems < 500 ? 0 : 50);
  cart.taxPrice = addDecimal(Number((0.18 * cart.itemsPrice).toFixed(2)));
  cart.totalPrice = (
    Number(cart.itemsPrice) +
    Number(cart.shippingPrice) +
    Number(cart.taxPrice)
  ).toFixed(2);

  const dispatch = useDispatch();

  const logoutHandler = () => {
    dispatch(logout());
  };
  return (
    <div>
      {/* Page Preloder */}
      <div id="preloder">
        <div className="loader" />
      </div>

      {/* Header Section Begin */}
      <Navbar bg="light" expand="lg">
        <Container>
          <Navbar.Brand href="#home">React-Bootstrap</Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="me-auto">
              <Nav.Link href="#home">Home</Nav.Link>
              <Nav.Link href="#link">Link</Nav.Link>
              <NavDropdown title="Dropdown" id="basic-nav-dropdown">
                <NavDropdown.Item href="#action/3.1">Action</NavDropdown.Item>
                <NavDropdown.Item href="#action/3.2">
                  Another action
                </NavDropdown.Item>
                <NavDropdown.Item href="#action/3.3">
                  Something
                </NavDropdown.Item>
                <NavDropdown.Divider />
                <NavDropdown.Item href="#action/3.4">
                  Separated link
                </NavDropdown.Item>
              </NavDropdown>
            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>
      <header className="header">
        <div className="container header__top fluid">
          <div className="row">
            <div className="col-lg-3">
              <div className="header__logo">
                <Link
                  to="/"
                  style={{
                    marginTop: "15px",
                    textDecoration: "none",
                    listStyle: "none",
                  }}
                >
                  <h3>Herbal Products</h3>
                </Link>
              </div>
            </div>
            <div className="col-lg-6" style={{ as }}>
              <nav className="header__menu">
                <ul>
                  <li className="active">
                    <Link to="/">Home</Link>
                  </li>
                  <li>
                    <Link to="/products">Products</Link>
                  </li>

                  <li>
                    <Link to="/cart">Cart</Link>
                  </li>
                  <li>
                    <Link to="/contact-us">Contact</Link>
                  </li>
                </ul>
              </nav>
            </div>
            <div className="col-lg-3">
              <div className="header__cart">
                <ul>
                  <li>
                    <Link to="/cart">
                      <i className="fa fa-shopping-bag" />
                      <span>{cart.cartItems.length}</span>
                    </Link>
                  </li>
                </ul>
                <div className="header__top__right__auth">
                  {userResult ? (
                    <NavDropdown
                      title={userResult.name}
                      id="username"
                      style={{ paddingTop: "10px" }}
                    >
                      <LinkContainer to="/profile">
                        <NavDropdown.Item>Profile</NavDropdown.Item>
                      </LinkContainer>
                      <NavDropdown.Item onClick={logoutHandler}>
                        Logout
                      </NavDropdown.Item>
                    </NavDropdown>
                  ) : (
                    <LinkContainer to="/login">
                      <Nav.Link>
                        <i className="fas fa-user"></i>
                        &nbsp; Login
                      </Nav.Link>
                    </LinkContainer>
                  )}
                </div>
              </div>
            </div>
          </div>
          <div className="humberger__open">
            <i
              className="fa fa-bars"
              onClick={() => openMenu()}
              style={{ marginTop: "15px" }}
            ></i>
          </div>
        </div>
      </header>
    </div>
  );
};

export default Header;
