import React, { useState } from "react";
import { Nav, NavDropdown, Navbar, Container } from "react-bootstrap";
import { LinkContainer } from "react-router-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import { logout } from "../../Redux/Action/AuthAction";

const Header = () => {
  const [menu, setMenu] = useState(true);

  const openMenu = () => {
    setMenu(!menu);
  };
  if (menu === true) {
    var as = { display: "none" };
  } else {
    as = { display: "block" };
    console.log(menu);
  }

  const userLogin = useSelector((state) => state.userLogin);
  const { userResult } = userLogin;

  const cart = useSelector((state) => state.cart);

  // console.log(cart);

  //fun for decimal
  const addDecimal = (num) => {
    return (Math.round(num * 100) / 100).toFixed(2);
  };
  cart.itemsPrice = addDecimal(
    cart.cartItems.reduce((acc, item) => acc + item.price * item.qty, 0)
  );

  cart.shippingPrice = addDecimal(cart.cartItems < 500 ? 0 : 50);
  cart.taxPrice = addDecimal(Number((0.18 * cart.itemsPrice).toFixed(2)));
  cart.totalPrice = (
    Number(cart.itemsPrice) +
    Number(cart.shippingPrice) +
    Number(cart.taxPrice)
  ).toFixed(2);

  const dispatch = useDispatch();

  const logoutHandler = () => {
    dispatch(logout());
  };
  return (
    <>
      <Navbar
        expand="lg"
        style={{ padding: "20px", backgroundColor: "#DAD5AB" }}
      >
        <Container>
          <Navbar.Brand>
            <Link
              style={{
                color: "blue",
                listStyle: "none",
                textDecoration: "none",
                fontSize: "22px",
                fontWeight: "600",
              }}
              to="/"
            >
              Herbal Product
            </Link>
          </Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="me-auto">
              <Link
                className="navbar-header-link"
                to="/"
                style={{
                  color: "cornflowerblue",
                  listStyle: "none",
                  textDecoration: "none",
                  padding: "5px 20px",
                  fontSize: "17px",
                  fontWeight: "500",
                }}
              >
                Home
              </Link>
              <Link
                className="navbar-header-link"
                style={{
                  color: "cornflowerblue",
                  listStyle: "none",
                  textDecoration: "none",
                  padding: "5px 20px",
                  fontSize: "17px",
                  fontWeight: "500",
                }}
                to="/products"
              >
                Products
              </Link>
              <Link className="navbar-header-link" to="/cart">
                Cart
              </Link>
              <NavDropdown title="Categories" id="basic-nav-dropdown">
                <NavDropdown.Item style={{ color: "black" }} to="#action/3.1">
                  Action
                </NavDropdown.Item>
                <NavDropdown.Item style={{ color: "black" }} to="#action/3.2">
                  Another action
                </NavDropdown.Item>
              </NavDropdown>
            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>
    </>
  );
};

export default Header;
