import React, { useState, useEffect } from "react";
import { Nav, NavDropdown, Navbar, Container } from "react-bootstrap";
import { LinkContainer } from "react-router-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import { logout } from "../../Redux/Action/AuthAction";
import { listCategories } from "../../Redux/Action/CategoryAction";
import "./Header.css";

const Header = () => {
  const dispatch = useDispatch();
  const [cate, setCate] = useState(false);
  const CategoryList = useSelector((state) => state.CategoryList);
  const { loading, error, categories } = CategoryList;

  useEffect(() => {
    dispatch(listCategories());
  }, [dispatch]);

  // console.log(cate);
  const openCategory = () => {
    setCate(!cate);
  };
  if (cate === true) {
    var as = { display: "none" };
  } else {
    as = { display: "block" };
  }
  const [menu, setMenu] = useState(true);

  const openMenu = () => {
    setMenu(!menu);
  };
  if (menu === true) {
    var as = { display: "none" };
  } else {
    as = { display: "block" };
    console.log(menu);
  }

  const userLogin = useSelector((state) => state.userLogin);
  const { userResult } = userLogin;

  const cart = useSelector((state) => state.cart);

  // console.log(cart);

  //fun for decimal
  const addDecimal = (num) => {
    return (Math.round(num * 100) / 100).toFixed(2);
  };
  cart.itemsPrice = addDecimal(
    cart.cartItems.reduce((acc, item) => acc + item.price * item.qty, 0)
  );

  cart.shippingPrice = addDecimal(cart.cartItems < 500 ? 0 : 50);
  cart.taxPrice = addDecimal(Number((0.18 * cart.itemsPrice).toFixed(2)));
  cart.totalPrice = (
    Number(cart.itemsPrice) +
    Number(cart.shippingPrice) +
    Number(cart.taxPrice)
  ).toFixed(2);

  const logoutHandler = () => {
    dispatch(logout());
  };
  return (
    <Navbar class expand="lg" style={{ backgroundColor: "#DAD5AB", display: "flex" }}>
      <Container>
        <Navbar.Brand>
          <Link
            style={{
              color: "blue",
              listStyle: "none",
              textDecoration: "none",
              fontSize: "22px",
              fontWeight: "600",
            }}
            to="/"
          >
            Herbal Product
          </Link>
        </Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="me-auto">
            <Link
              className="navbar-header-link"
              to="/"
              style={{
                color: "black",
                listStyle: "none",
                textDecoration: "none",
                padding: "5px 20px",
                fontSize: "17px",
                fontWeight: "500",
              }}
            >
              Home
            </Link>
            <Link
              className="navbar-header-link"
              style={{
                color: "black",
                listStyle: "none",
                textDecoration: "none",
                padding: "5px 20px",
                fontSize: "17px",
                fontWeight: "500",
              }}
              to="/products"
            >
              Products
            </Link>
            <Link
              className="navbar-header-link"
              style={{
                color: "black",
                listStyle: "none",
                textDecoration: "none",
                padding: "5px 20px",
                fontSize: "17px",
                fontWeight: "500",
              }}
              to="/cart"
            >
              Cart
            </Link>
            <div>
              <NavDropdown title="Categories">
                {categories.map((category, index) => (
                  <LinkContainer
                    to={`/products/${category._id}`}
                    key={index}
                    style={{
                      color: "black",
                      listStyle: "none",
                      textDecoration: "none",
                      fontSize: "17px",
                      fontWeight: "500",
                    }}
                  >
                    <NavDropdown.Item style={{ padding: "28px 4px" }}>
                      {category.title}
                    </NavDropdown.Item>
                  </LinkContainer>
                ))}
              </NavDropdown>
            </div>

            <div className="active" style={{ marginLeft: "380px" }}>
              {userResult ? (
                <NavDropdown
                  title={userResult.name}
                  id="username"
                  style={{ paddingTop: "10px" }}
                >
                  <LinkContainer
                    to="/profile"
                    style={{
                      color: "black",
                      listStyle: "none",
                      textDecoration: "none",
                      padding: "5px 20px",
                      fontSize: "17px",
                      fontWeight: "500",
                    }}
                  >
                    <NavDropdown.Item>Profile</NavDropdown.Item>
                  </LinkContainer>
                  <NavDropdown.Item
                    onClick={logoutHandler}
                    style={{
                      color: "black",
                      listStyle: "none",
                      textDecoration: "none",
                      padding: "5px 20px",
                      fontSize: "17px",
                      fontWeight: "500",
                    }}
                  >
                    Logout
                  </NavDropdown.Item>
                </NavDropdown>
              ) : (
                <LinkContainer
                  to="/login"
                  style={{
                    color: "black",
                    listStyle: "none",
                    textDecoration: "none",
                    padding: "5px 20px",
                    fontSize: "17px",
                    fontWeight: "500",
                  }}
                >
                  <Nav.Link>
                    <i className="fas fa-user"></i>
                    &nbsp; Login
                  </Nav.Link>
                </LinkContainer>
              )}
            </div>
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
};

export default Header;
